import React, {Component} from 'react'; 
import Album from './album'

import axios from "axios" 

class ViewDataAPI extends Component{

    state = {
        posts: [],
        images:[]
    }

    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/comments')
        axios.get('https://jsonplaceholder.typicode.com/photos')
        .then(response => {
            this.setState({
                posts: response.data,
                images: response.data
            })
        })
        .catch(error => console.log('error'))
    }
 
    render(){
        let { posts } = this.state 

        if(posts.length === 0){
            return <h1>Loading...</h1>
        }
        else{
            return(
                <div className="header">
                    <div className="container">
                    <div className="row">
                            <div className="col-lg-12 col-sm-12 col-xs-12">
                                <div className="text-center mb-20">
                                    <h1>View Data From API</h1>
                                    <p>using jsx-component, props, map in react </p>
                                </div>
                            </div>
                        </div> 
                        <div className="row">  
                                {
                                    posts.map(post => 
                                        {
                                            return <Album  name={post.name} email = {post.email} body = {post.body} thumb={post.images.thumbnailUrl}   />
                                        }
                                    )
                                }   
                                
                                {
                                    posts.map(post => 
                                        {
                                            return <Album  thumb={post.images.thumbnailUrl}   />
                                        }
                                    )
                                } 
                        </div>
                    </div>
                </div>
            )
        }

      
    }
}

export default ViewDataAPI;
